sp_widget.client_script:
api.controller = function($scope, $rootScope) {
    /* widget controller */
    var c = this;

	  c.updateNote = function(updateType) {
    c.server.get({
      action: 'updateNote',
      noteID: c.data.noteID,
      noteBody: c.data.note,
      noteTitle: c.data.title
         }).then(function(r) {
            });
    }

    $rootScope.$on('selectNote', function(event, data) {
        c.server.get({
			action: 'getNote',
			noteID: $rootScope.noteID
        }).then(function(r) {
			c.data.title = r.data.note.title;
			c.data.note = r.data.note.note;
			c.data.noteID = r.data.note.sys_id;
			c.data.number = r.data.note.number;
			c.data.created = r.data.note.sys_created_on;
		});

    });



};

sp_widget.script:
(function() {
    /* populate the 'data' object */
    /* e.g., data.table = $sp.getValue('table'); */

    if (input && input.noteID) {
		var gr1 = new GlideRecord('x_snc_createnotes_note');

		if (gr1.get(input.noteID)) {
			if (input.action == 'getNote') {
				data.note = {};
				$sp.getRecordValues(data.note, gr1, "title, note, sys_id, number, sys_created_on");
			}
			else if (input.action == 'updateNote') { 
    					gr1.title = input.noteTitle; 
    				  gr1.note = input.noteBody; 
   						gr1.update(); 
  				}
	   	}
    }
})();
